document.addEventListener('DOMContentLoaded', () => {

    // Seleciona os elementos da tabela
    const disponivelElements = [...document.querySelectorAll('.table-disponivel p')];
    const percentualElements = [...document.querySelectorAll('.table-percentual p')];

    // Lógica para alterar as cores com base no consumido
    percentualElements.forEach((el, idx) => {
        // Remove o % e converte para número
        const taxa = parseFloat(el.textContent.replace('%', ''));

        // Verifica se é maior que 70%
        if (taxa > 70) {
            disponivelElements[idx].style.color = '#ff0000';
            el.style.color = '#ff0000';
        }
    });


    // Lógica coluna taxa de referência
    const contractStartDate = new Date('YYYY-MM-DD'); // Atualize a data referente ao período de contrato do cliente
    const contractEndDate = new Date('YYYY-MM-DD'); // Atualize a data referente ao período de contrato do cliente

    // Data atual
    const currentDate = new Date();
    let monthsElapsed = 0

    // Calcula o total de meses de contrato
    const totalMonths = (contractEndDate.getFullYear() - contractStartDate.getFullYear()) * 12 + (contractEndDate.getMonth() - contractStartDate.getMonth()) + 1;

    // Calcula meses decorridos até o momento
    if (currentDate >= contractStartDate && currentDate <= contractEndDate) {
        monthsElapsed = (currentDate.getFullYear() - contractStartDate.getFullYear()) * 12 + (currentDate.getMonth() - contractStartDate.getMonth()) + 1;
    } else if (currentDate > contractEndDate) {
        monthsElapsed = totalMonths;
    }

    //Seleciona a coluna da taxa de referência

    const supermessagesEsperado = document.getElementById('supermessages-expected');
    const smsEsperado = document.getElementById('sms-expected');
    const whatsEsperado = document.getElementById('whatsapp-expected');

    supermessagesEsperado.textContent = `${((monthsElapsed / totalMonths) * 100).toFixed(2)}%`;
    smsEsperado.textContent = `${((monthsElapsed / totalMonths) * 100).toFixed(2)}%`;
    whatsEsperado.textContent = `${((monthsElapsed / totalMonths) * 100).toFixed(2)}%`;

});

