* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Space Grotesk', 'Inter', sans-serif;
}

main {
    min-height: 70vh;
}

header {
    border-bottom: 2px solid #ddd;
}

header .header-top {
    background-color: #FC4706;
}

.header-top h2 {
    color: #fff;
    font-size: 16px;
    padding-top: 8px;
}

.header-content {
    justify-content: space-between;
    align-items: center;
    margin: 1rem auto;
}


.main-title {
    margin-top: 12px;
}

.main-title h1 {
    margin: 18px auto;
    font-size: 2rem;
}

.table-wrapper {
    overflow-x: hidden;
    width: 100%;
}


.table-title h2 {
    font-size: 1.2rem;
    text-transform: uppercase;
    font-weight: 700;
}

.table-title {
    margin-top: 18px;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
    width: 100%;
    text-align: center;
}

#contratado {
    grid-column-start: 2;
}

.table-content {
    margin-bottom: 18px;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
    width: 100%;
    text-align: center;
}

.table-services h3 {
    margin: 16px auto;
    margin-bottom: 18px;
    font-size: 1.5rem;
    text-align: left;
}

.table-content p {
    margin: 16px auto;
    font-size: 1.3rem;
    font-weight: bold;
}

.table-contratado p {
    color: #92a0ad;
}

.table-disponivel p {
    color: #38cb82;
}

.table-consumido p {
    color: #ff0000;
}

.table-percentual p {
    color: #38cb82;
}

.table-month {
    width: 80%;
    max-width: 600px;
    text-align: center;
    margin: 16px auto 28px auto;
    background-color: #f9f9f9;
    border-radius: 25px;
    box-shadow: rgba(149, 157, 165, 0.2) 0px 8px 24px;
}

thead {
    text-transform: uppercase;
}

tr,
th,
td {
    padding: 12px 5px;
}

footer {
    background-color: #1E1E1E;
    padding: 28px 0;
}

.acompanhamento {
    color: #fff;
    font-weight: 400;
    margin: 1.5rem auto;
}

footer .footer-content {
    justify-content: space-between;
}

.acompanhamento span {
    font-weight: 700;
    margin-left: 24px;
}

.social ul {
    list-style-type: none;
    padding: 0;
    margin: 0;
}

.social ul li {
    display: inline-block;
    margin-right: 10px;
}

.social a {
    text-decoration: none;
}

.social a i {
    color: #fff;
    font-size: 38px;
    margin: 0 12px;
}

.social a i:hover {
    transition: all 0.2s ease-in;
    opacity: 0.5;
}


@media only screen and (max-width:992px) {

    .main-title h1 {
        font-size: 1.2rem;
    }

    .table-title h2 {
        font-size: 1rem;
    }

    .table-services h3 {
        font-size: 1rem;
    }

    .table-content p {
        font-size: 1rem;
    }

    .acompanhamento p {
        font-size: 1rem;
    }
}

@media only screen and (max-width:576px) {

    .header-content {
        justify-content: center;
    }

    .main-title h1 {
        font-size: 1.2rem;
    }

    .table-wrapper {
        overflow-x: auto;
    }

    .table-title,
    .table-content {
        min-width: 600px;
    }

    .table-title h2 {
        font-size: 0.85rem;
    }

    .table-services h3 {
        font-size: 0.85rem;
        margin-bottom: 20px;
    }

    .table-content p {
        font-size: 0.85rem;
    }

    tr,
    th,
    td {
        font-size: 0.85rem;
    }


    .acompanhamento {
        text-align: center;
    }

    footer .footer-content {
        justify-content: center;
    }

}

